import React from 'react';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import '../styles/App.css';

const useStyles = makeStyles((theme) => ({
  joinButton: {
    backgroundColor: '#EB5757',
    color: 'white',
    '&:hover': {
      backgroundColor: '#b03e3e',
      color: 'white'
    },
    textTransform: 'none'
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'left',
    color: theme.palette.text.secondary,
  },
  container: {
    maxWidth: '100%'
  }
}));

function App() {
  const classes = useStyles();

  return (
    <div className="App">
      <div className="App-banner">
        <p>
          A Fully Connected, Centralized BU.
        </p>
        <Button className={classes.joinButton}>
          Join Today
        </Button>
      </div>

      <Typography component="h1">Connect with Peers and Mentors</Typography>
      <Grid container spacing={2} justify="center" alignItems="center" zeroMinWidth className={classes.container}>
        <Grid item xs={10} sm={5}>
          <div className={classes.paper}>
            <h2>Clubs</h2>
            <p>
              From beekeeping to photography, rocket propulsion to religion, BU has more than 500 student organizations to engage almost any interest. Find and connect with fellow students who share your interests.
            </p>
          </div>
        </Grid>
        <Grid item xs={10} sm={5}>
          <div className={classes.paper}>
            <h2>School, Major, and Classes Taken</h2>
          </div>
        </Grid>
        <Grid item xs={10} sm={5}>
          <div className={classes.paper}>
            <h2>Interests</h2>
          </div>
        </Grid>
        <Grid item xs={10} sm={5}>
          <div className={classes.paper}>
            <h2>Research</h2>
          </div>
        </Grid>
      </Grid>


      <br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br />
    </div>
  );
}

export default App;
